// ===== ETAPA 1: RECEBER E SEPARAR OS DADOS =====
// $input.all() recebe os resultados de TODOS os n√≥s que se conectam a este.
// Neste caso, ele receber√° uma lista de itens dos produtos e dos contatos.
const todosOsItens = $input.all();

// Criamos arrays vazios para organizar os dados
const produtos = [];
const contatos = [];

// Percorremos todos os itens recebidos para separ√°-los
for (const item of todosOsItens) {
  // Se o item tem a propriedade 'id_produto', √© um produto.
  if (item.json.hasOwnProperty('id_produto')) {
    produtos.push(item.json);
  }
  // Se o item tem a propriedade 'id_lead', √© um contato.
  if (item.json.hasOwnProperty('id_lead')) {
    contatos.push(item.json);
  }
}

// ===== ETAPA 2: APLICAR OS FILTROS QUE N√ÉO FORAM POSS√çVEIS NOS N√ìS ANTERIORES =====

// Filtro 1: Manter apenas produtos com quantidade maior que 0.
const produtosComEstoque = produtos.filter(p => {
  const quantidade = parseInt(p.quantidade) || 0;
  return quantidade > 0;
});

// Filtro 2: Manter apenas contatos que N√ÉO s√£o 'convertido' ou 'opt_out'.
const contatosValidos = contatos.filter(c => {
  const status = c.status || '';
  return status !== 'convertido' && status !== 'opt_out';
});


// ===== ETAPA 3: VERIFICA√á√ïES DE SEGURAN√áA =====
// Se ap√≥s os filtros n√£o sobrar nada, interrompemos o workflow com uma mensagem clara.
if (produtosComEstoque.length === 0) {
  throw new Error('‚ùå Nenhum produto com estoque dispon√≠vel foi encontrado. Workflow interrompido.');
}
if (contatosValidos.length === 0) {
  throw new Error('‚ùå Nenhum contato v√°lido para prospec√ß√£o foi encontrado. Workflow interrompido.');
}


// ===== ETAPA 4: COMBINAR LEADS E PRODUTOS =====
// Agora, com as listas filtradas, criamos os pares.
const pares = [];
for (const contato of contatosValidos) {
  // Para cada contato, selecionamos um produto aleat√≥rio da lista de produtos com estoque.
  const produtoAleatorio = produtosComEstoque[Math.floor(Math.random() * produtosComEstoque.length)];

  pares.push({
    // Dados do Contato
    id_lead: contato.id_lead,
    nome_lead: contato.nome,
    telefone: contato.telefone,
    empresa: contato.empresa,
    segmento: contato.segmento,
    score_atual: parseInt(contato.score) || 0,

    // Dados do Produto
    id_produto: produtoAleatorio.id_produto,
    nome_produto: produtoAleatorio.nome_produto,
    descricao_produto: produtoAleatorio.descricao,
    preco_normal: parseFloat(produtoAleatorio.preco_normal) || 0,
    preco_promo: parseFloat(produtoAleatorio.preco_promo) || 0,
    link_foto: produtoAleatorio.link_foto || ''
  });
}

// ===== ETAPA 5: RETORNAR OS DADOS PARA O PR√ìXIMO N√ì =====
// O N8N espera que o retorno seja um array de objetos, cada um com uma chave 'json'.
return pares.map(item => ({ json: item }));

