// A resposta do AI Agent vem em formato diferente
// Precisamos adaptar baseado no output real

let resposta;

try {
  // Tentar parsear se vier como string
  if (typeof $json.output === 'string') {
    resposta = JSON.parse($json.output);
  } else if (typeof $json.text === 'string') {
    resposta = JSON.parse($json.text);
  } else if ($json.output && typeof $json.output === 'object') {
    resposta = $json.output;
  } else {
    // Fallback: tentar pegar todo o json
    resposta = $json;
  }
} catch (e) {
  throw new Error('‚ùå Erro ao processar resposta da IA: ' + e.message + '
Resposta recebida: ' + JSON.stringify($json));
}

// Validar campos obrigat√≥rios
if (!resposta.score_fit || !resposta.mensagem) {
  throw new Error('‚ùå IA n√£o retornou os campos esperados. Resposta: ' + JSON.stringify(resposta));
}

return {
