// Recebe a lista de itens enviados com dados ricos.
const itensEnviados = $input.all();

// Se o input estiver vazio, retorna um relat√≥rio zerado para manter a consist√™ncia do fluxo.
if (itensEnviados.length === 0) {
  return [{
    json: {
      total_analisados: 0, total_enviados: 0, total_rejeitados: 0, score_medio: 0,
      detalhes_adicionais: {
        performance_score: { minimo: 0, maximo: 0 },
        performance_temporal: { duracao_segundos: 0 },
        distribuicao: { por_produto: {}, por_segmento: {}, por_empresa: {} },
        leads_contatados: []
      },
      timestamp: new Date().toISOString()
    }
  }];
}

// =================================================================
// ETAPA 2: EXTRA√á√ÉO E AGREGA√á√ÉO DE DADOS
// =================================================================

// Inicializa as vari√°veis para as m√©tricas do relat√≥rio.
let somaDosScores = 0;
let scoreMinimo = Infinity;
let scoreMaximo = -Infinity;
const distribuicaoProdutos = {};
const distribuicaoSegmentos = {};
const distribuicaoEmpresas = {};
const leadsContatados = [];

// Loop principal para processar cada item enviado.
for (const item of itensEnviados) {
  const { score, produto_relacionado, segmento, empresa, nome_lead } = item.json;

  // 1. Agrega√ß√µes de Score
  if (typeof score === 'number') {
    somaDosScores += score;
    if (score < scoreMinimo) scoreMinimo = score;
    if (score > scoreMaximo) scoreMaximo = score;
  }

  // 2. Agrega√ß√µes de Distribui√ß√£o (Produto, Segmento, Empresa)
  if (produto_relacionado) {
    distribuicaoProdutos[produto_relacionado] = (distribuicaoProdutos[produto_relacionado] || 0) + 1;
  }
  if (segmento) {
    distribuicaoSegmentos[segmento] = (distribuicaoSegmentos[segmento] || 0) + 1;
  }
  if (empresa) {
    distribuicaoEmpresas[empresa] = (distribuicaoEmpresas[empresa] || 0) + 1;
  }

  // 3. Lista de Leads Contatados para Auditoria
  leadsContatados.push({
    nome: nome_lead,
    empresa: empresa,
    score: score
  });
}

// =================================================================
// ETAPA 3: C√ÅLCULOS FINAIS E MONTAGEM DO RELAT√ìRIO
// =================================================================

const totalEnviados = itensEnviados.length;
const scoreMedio = totalEnviados > 0 ? somaDosScores / totalEnviados : 0;

// Calcula a dura√ß√£o total da opera√ß√£o a partir dos timestamps.
const timestamps = itensEnviados.map(item => new Date(item.json.timestamp));
const primeiroTimestamp = new Date(Math.min.apply(null, timestamps));
const ultimoTimestamp = new Date(Math.max.apply(null, timestamps));
const duracaoSegundos = (ultimoTimestamp - primeiroTimestamp) / 1000;

// Estrutura o JSON de sa√≠da de forma organizada e rica.
const relatorioFinal = {
  // N√≠vel 1: Dados principais para a mensagem r√°pida
  total_analisados: totalEnviados,
  total_enviados: totalEnviados,
  total_rejeitados: 0,
  score_medio: parseFloat(scoreMedio.toFixed(1)),
  timestamp: new Date().toISOString(),

  // N√≠vel 2: Detalhes aprofundados para an√°lise
  detalhes_adicionais: {
    performance_score: {
      minimo: scoreMinimo,
      maximo: scoreMaximo
    },
    performance_temporal: {
      duracao_segundos: duracaoSegundos,
      primeiro_disparo_em: primeiroTimestamp.toISOString(),
      ultimo_disparo_em: ultimoTimestamp.toISOString()
    },
    distribuicao_disparos: {
      por_produto: distribuicaoProdutos,
      por_segmento: distribuicaoSegmentos,
      por_empresa: distribuicaoEmpresas
    },
    leads_contatados: leadsContatados
  }
};

return [{
  json: relatorioFinal
}];

